import React from "react";
import Input from "../components/input";
import {changeLanguage} from "../api/apiCall";
import {withTranslation} from "react-i18next";

class LoginPage extends React.Component{
    state={
        username:null,
        password:null
    };

    onChange = event =>{
        const{name,value}=event.target;
        this.setState({
            [name]:value
        })
    }

    onChangeLanguage = language =>{
        const {i18n} = this.props;
        i18n.changeLanguage(language);
        changeLanguage(language);
    };

    render(){
        return(
            <div className="container">
                <form>
                    <h1 className="text-center">
                        Login
                    </h1>
                    <Input name="username" label="Username" onChange={this.onChange} ></Input>
                    <Input name="password" label="Password" type="password" onChange={this.onChange}></Input>
                    <div className="text-center">
                        <button className="btn btn-primary">
                           Login
                        </button>
                    </div>
                </form>
                <div>
                    <img src="https://flagcdn.com/48x36/tr.png" alt="Turkish Flag" onClick={()=>this.onChangeLanguage('tr')} style={{cursor:'pointer'}}/>
                    <img src="https://flagcdn.com/48x36/gb.png" alt="USA Flag" onClick={()=>this.onChangeLanguage('en')} style={{cursor:'pointer'}}/>
                </div>
            </div>
        );
    }
}

const LoginPageWithTranslation = withTranslation()(LoginPage);

export default LoginPageWithTranslation;
